import React from "react";
import styles from "../styles/forecast-table.module.scss";
import { TempDisplay } from "./TempDisplay";
import { WeatherDisplay } from "../store";

type ForecastTableProps = {
  forecast: WeatherDisplay[];
};

export const ForecastTable: React.FC<ForecastTableProps> = ({ forecast }) => (
  <table className={styles.forecastTable}>
    <thead>
      <tr className={styles.header}>
        <th>Time</th>
        <th>Temp</th>
        <th>Weather</th>
        <th>Alerts</th>
      </tr>
    </thead>
    <tbody>
      {forecast.map((tf, index) => (
        <tr key={index} className={styles.body}>
          <td>{tf.time}</td>
          <td className="forecast-table-temp">
            <TempDisplay temp={tf.temp} />
          </td>
          <td className={styles.bodyImage}>
            <img src={tf.icon} alt={`${tf.main} icon`} />
            <span>{tf.main}</span>
          </td>
        </tr>
      ))}
    </tbody>
  </table>
);
