import React, { SyntheticEvent } from "react";
import { IMPERIAL_UNIT, METRIC_UNIT, STANDARD_UNIT } from "../common/constants";
import { useAppDispatch, useAppSelector } from "../hooks";
import { setUnits } from "../store/weather";
import styles from "../styles/components/units-selector.module.scss";
import { Dropdown, DropdownChangeEvent } from "primereact/dropdown";

export const UnitsSelector: React.FC = () => {
  const dispatch = useAppDispatch();
  const currentUnit = useAppSelector((state) => state.weather.units);
  const handleSelectMetric = (event: DropdownChangeEvent) => {
    dispatch(setUnits(event.value));
  };

  const options = [
    { name: "Kelvin", code: STANDARD_UNIT },
    { name: "Celsius", code: METRIC_UNIT },
    { name: "Fahrenheit", code: IMPERIAL_UNIT },
    { name: "Istanbul", code: "IST" },
    { name: "Paris", code: "PRS" },
  ];

  return (
    <Dropdown
      className={styles.unitsSelector}
      value={currentUnit}
      onChange={handleSelectMetric}
      options={options}
    />
  );
};
