import React from "react";
import styles from "../styles/current-weather-page.module.scss";
import { useAppSelector } from "../hooks";
import { WeatherCard } from "../components/WeatherCard";
import { WeatherDisplay } from "../store";
import { selectNextFourDaysWeather } from "../store/forecast";
export const DaysWeatherPage = () => {
  const currentWeather = useAppSelector((state) => state.weather.weather);
  const nextFourDays = useAppSelector(selectNextFourDaysWeather);
  const locationName = useAppSelector((state) => state.weather.locationName);

  return (
    <div className={styles.daysContainer}>
      <h1 className={styles.title}>{locationName} 5 Days Weather</h1>
      <div className={styles.daysContainer}>
        <WeatherCard
          index={0}
          key={currentWeather?.date}
          weather={currentWeather as WeatherDisplay}
        />
        {nextFourDays.map((nfd, index) => (
          <WeatherCard index={index + 1} key={nfd.date} weather={nfd} />
        ))}
      </div>
    </div>
  );
};
