import React from "react";
import { useAppDispatch, useAppSelector } from "../hooks";
import { Menubar } from "primereact/menubar";
import styled from "styled-components";
import {
  CELSIUS,
  FAHRENHEIT,
  IMPERIAL_UNIT,
  KELVIN,
  METRIC_UNIT,
  STANDARD_UNIT,
} from "../common/constants";
import { setUnits } from "../store/weather";
import { useNavigate } from "react-router-dom";

const StyledTopBar = styled(Menubar)`
  border-radius: 0;
  width: 100%;
`;

export const TopBar = () => {
  const dispatch = useAppDispatch();
  const navigate = useNavigate();
  const locationName = useAppSelector((state) => state.weather.locationName);
  const units = useAppSelector((state) => state.weather.units);

  const items = [
    {
      label: "Weather",
      command: () => navigate("/"),
    },
    {
      label: "Units",
      items: [
        {
          label: KELVIN,
          icon: units === STANDARD_UNIT ? "pi pi-check" : "",
          command: () => dispatch(setUnits(STANDARD_UNIT)),
        },
        {
          label: FAHRENHEIT,
          icon: units === IMPERIAL_UNIT ? "pi pi-check" : "",
          command: () => dispatch(setUnits(IMPERIAL_UNIT)),
        },
        {
          label: CELSIUS,
          icon: units === METRIC_UNIT ? "pi pi-check" : "",
          command: () => dispatch(setUnits(METRIC_UNIT)),
        },
      ],
    },
    {
      label: `Location: ${locationName}`,
    },
  ];

  return <StyledTopBar model={items} />;
};
