import { Card } from "primereact/card";
import React from "react";
import styled from "styled-components";
import { WeatherDisplay } from "../store";
import { TempDisplay } from "./TempDisplay";
import { Image } from "primereact/image";

type MiniWeatherCardsProps = {
  forecast: WeatherDisplay;
};

const StyledCard = styled(Card)`
  background-color: blue;
  width: 100px;
  height: fit-content;
  color: white;
  border-radius: 10%;

  .p-card-body {
    padding: 0;
  }

  .p-card-content {
    display: flex;
    flex-direction: column;
  }
`;

const StyledCardDate = styled.span`
  font-size: 18px;
  font-weight: 500;
  margin-bottom: 3px;
  padding-left: 18px;
`;

const StyledTemp = styled(TempDisplay)`
  font-size: 24px;
  font-weight: 600;
  padding-left: 18px;
`;

const StyledWeatherIcon = styled(Image)`
  width: 50px;
  height: 50px;
`;

export const MiniWeatherCards: React.FC<MiniWeatherCardsProps> = ({
  forecast,
}) => {
  return (
    <StyledCard>
      <StyledCardDate>{forecast.time}</StyledCardDate>
      <StyledWeatherIcon
        src={forecast.icon}
        alt={`${forecast.description} icon`}
      />
      <StyledTemp temp={forecast.temp} />
    </StyledCard>
  );
};
