import React from "react";
import styles from "../styles/pages/days-weather-page.module.scss";
import { useAppSelector } from "../hooks";
import { WeatherCard } from "../components";
import { Carousel } from "primereact/carousel";
import { WeatherDisplay } from "../store";
import styled from "styled-components";

const StyledCarousel = styled(Carousel)`
  .p-carousel-item {
    margin-right: 3rem;
  }
`;

export const DaysWeatherPage = () => {
  const daysWeather = useAppSelector((state) => state.weather.weather);

  const weathers = Object.entries(daysWeather).map(([id, { weather }]) => ({
    id,
    ...weather,
  }));

  const responsiveOptions = [
    {
      breakpoint: "1199px",
      numVisible: 1,
      numScroll: 1,
    },
    {
      breakpoint: "991px",
      numVisible: 2,
      numScroll: 1,
    },
    {
      breakpoint: "767px",
      numVisible: 1,
      numScroll: 1,
    },
  ];

  const weatherTemplate = (weather: WeatherDisplay & { id: string }) => (
    <WeatherCard id={weather.id} weather={weather} />
  );

  return (
    <div className={styles.daysWeather}>
      <h1 className={styles.title}>5 Days Weather</h1>
      {!!weathers.length && (
        <StyledCarousel
          value={weathers}
          numScroll={0}
          numVisible={5}
          responsiveOptions={responsiveOptions}
          itemTemplate={weatherTemplate}
        />
      )}
    </div>
  );
};
