import React from "react";
import { MetricSystemSelector } from "./MetricSystemSelector";
import styles from "../styles/top-bar.module.scss";
import { useAppSelector } from "../hooks";
export const TopBar = () => {
  const locationName = useAppSelector((state) => state.weather.locationName);
  return (
    <div className={styles.topBarContainer}>
      <div className={styles.location}>Location: {locationName}</div>
      <MetricSystemSelector />
    </div>
  );
};
