import React, { SyntheticEvent } from "react";
import { IMPERIAL_UNIT, METRIC_UNIT, STANDARD_UNIT } from "../common/constants";
import { useAppDispatch, useAppSelector } from "../hooks";
import { setUnits } from "../store/weather";
import styles from "../styles/units-selector.module.scss";

export const UnitsSelector: React.FC = () => {
  const dispatch = useAppDispatch();
  const currentUnit = useAppSelector((state) => state.weather.units);
  const handleSelectMetric = (event: SyntheticEvent<HTMLSelectElement>) => {
    const target = event.target as HTMLSelectElement;
    dispatch(setUnits(target.value));
  };

  return (
    <select
      className={styles.unitsSelector}
      defaultValue={currentUnit}
      onChange={handleSelectMetric}
    >
      <option value={STANDARD_UNIT}>Kelvin</option>
      <option value={METRIC_UNIT}>Celsius</option>
      <option value={IMPERIAL_UNIT}>Fahrenheit</option>
    </select>
  );
};
