import React from "react";
import { WeatherDisplay } from "../store";
import { MiniWeatherCard } from "./MiniWeatherCard";
import styled from "styled-components";

type ForecastTableProps = {
  forecast: WeatherDisplay[];
};

const StyledForecastContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;

  & > div {
    align-self: center;
    margin-right: 3rem;
    margin-bottom: 3rem;
  }

  @media (max-width: 992px) {
    flex-direction: column;

    & > div {
      margin-right: 0;
    }
  }
`;

export const ForecastTable: React.FC<ForecastTableProps> = ({ forecast }) => (
  <StyledForecastContainer>
    {forecast.map((value) => (
      <MiniWeatherCard key={value.time} forecast={value} />
    ))}
  </StyledForecastContainer>
);
