import { convertToHoursMinutes, convertToMonthDay } from "./date.util";
import { ForecastResponse, WeatherResponse } from "../api";
import { ForecastDisplay, WeatherData } from "../store";
import { ForecastData } from "../api/types";
import moment from "moment";

export const convertMonthDayToId = (date: string) =>
  date.split(" ").join("-").toLowerCase();

export const reduceForecastData = (data: ForecastResponse) => {
  const reduced = data.list.reduce(
    (acc: { [key: string]: ForecastDisplay[] }, current: ForecastData) => {
      const key = convertToMonthDay(current.dt_txt);
      const forecast: ForecastDisplay = {
        time: convertToHoursMinutes(key),
        temp: Math.round(current.main.temp),
        icon: `${process.env.REACT_APP_WEATHER_ICONS_URL}/${current.weather[0].icon}@2x.png`,
        main: current.weather[0].main,
      };

      if (!acc[key]) {
        acc[key] = [forecast];

        return acc;
      }

      acc[key].push(forecast);

      return acc;
    },
    {},
  );

  return Object.entries(reduced).splice(0, 4);
};

export const parseWeatherForecastData = (data: {
  weatherData: WeatherResponse;
  forecastData: ForecastResponse;
}): WeatherData => {
  const result: WeatherData = {};

  const today = data.weatherData;
  const todayWeather = data.weatherData.weather[0];
  const todayKey = convertMonthDayToId(new Date().toString());

  const forecasts = reduceForecastData(data.forecastData);

  result[todayKey] = {
    weather: {
      date: convertToMonthDay(),
      temp: Math.round(today.main.temp),
      icon: `${process.env.REACT_APP_WEATHER_ICONS_URL}/${todayWeather.icon}@2x.png`,
      description:
        todayWeather.description.charAt(0).toUpperCase() +
        todayWeather.description.slice(1), // make the first letter of the description upper case
    },
    forecast: forecasts[0][1],
  };

  forecasts.splice(1, 4).forEach((value) => {
    result[value[0]] = {
      weather: value[1][Math.floor(moment().hour() / 3)],
      forecast: value[1],
    };
  });

  return result;
};
