import React, { HTMLAttributes } from "react";
import { useNavigate } from "react-router-dom";
import { TempDisplay } from "./TempDisplay";
import { WeatherDisplay } from "../store";
import { Card } from "primereact/card";
import styled from "styled-components";
import { Image } from "primereact/image";
import { Skeleton } from "primereact/skeleton";

type WeatherCardType = {
  id?: string;
  weather: WeatherDisplay;
} & Omit<HTMLAttributes<Card>, "">;

const StyledCard = styled(Card)`
  background-color: blue;
  cursor: pointer;
  width: 250px;
  height: 250px;
  color: white;
  border-radius: 5%;

  .p-card-body {
    padding: 0;
  }

  .p-card-content {
    display: flex;
    flex-direction: column;
  }
`;

const StyledCardDate = styled.span`
  font-size: 18px;
  font-weight: 500;
  margin-bottom: 3px;
  padding-left: 18px;
`;

const StyledTemp = styled(TempDisplay)`
  font-size: 38px;
  font-weight: 600;
  padding-left: 18px;
`;

const StyledWeatherIcon = styled(Image)`
  width: 100px;
  height: 100px;
`;

const StyledDescription = styled.span`
  font-size: 24px;
  padding-left: 18px;
`;

const StyledSkeleton = styled(Skeleton)``;

export const WeatherCard: React.FC<WeatherCardType> = ({
  weather,
  id,
  style,
}) => {
  const navigate = useNavigate();
  const redirectToForecast = () =>
    id ? navigate(`/weather-forecast/${id}`) : null;

  return (
    <StyledCard onClick={redirectToForecast} style={style}>
      <Skeleton height="100%" />
      {/*<StyledCardDate>{weather.date}</StyledCardDate>*/}
      {/*<StyledTemp temp={weather.temp} />*/}
      {/*<StyledWeatherIcon*/}
      {/*  src={weather.icon}*/}
      {/*  alt={`${weather.description} icon`}*/}
      {/*/>*/}
      {/*<StyledDescription>{weather.description}</StyledDescription>*/}
    </StyledCard>
  );
};
