import React, { useEffect } from 'react';
import styles from '../styles/current-weather-page.module.scss';
import { useAppDispatch, useAppSelector } from '../hooks';
import { WeatherCard } from '../components/WeatherCard';
import { WeatherDisplay } from '../store/types';
import { setCurrentWeathers } from '../store/forecast';
export const DaysWeatherPage = () => {
	const dispatch = useAppDispatch();
	const currentWeather = useAppSelector((state) => state.weather.weather);
	const nextFourDays = useAppSelector(
		(state) => state.forecast.currentWeathers
	);
	const locationName = useAppSelector((state) => state.weather.locationName);

	useEffect(() => {
		dispatch(setCurrentWeathers());
	}, []);

	return (
		<div className={styles.daysContainer}>
			<h1 className={styles.title}>{locationName} 5 Days Weather</h1>
			<div className={styles.daysContainer}>
				<WeatherCard weather={currentWeather as WeatherDisplay} />
				{nextFourDays.map((nfd) => (
					<WeatherCard key={nfd.date} weather={nfd} />
				))}
			</div>
		</div>
	);
};
