import React from "react";
import { useParams } from "react-router-dom";
import { ForecastTable, WeatherCard } from "../components";
import { useAppSelector } from "../hooks";
import styles from "../styles/pages/forecast-page.module.scss";
import { Skeleton } from "primereact/skeleton";
export const ForecastPage: React.FC = () => {
  const { id } = useParams();
  const isLaoding = useAppSelector((state) => state.weather.isLoading);
  const selectedDayForecast = useAppSelector(
    (state) => state.weather.weather[id as string],
  );

  const loadingTemplate = () =>
    Array(5)
      .fill("data")
      .map(() => (
        <div style={{ width: "250px", height: "250px" }}>
          <Skeleton height="250px" width="250px" />
        </div>
      ));

  return isLoading ? (
    loadingTemplate()
  ) : (
    <>
      <h1 className={styles.title}>Forecast</h1>
      <WeatherCard
        style={{ marginBottom: "4rem" }}
        weather={selectedDayForecast.weather}
      />
      <ForecastTable forecast={selectedDayForecast.forecast} />
    </>
  );
};
