import React from 'react';
import { useLocation } from 'react-router-dom';
import { WeatherCard } from '../components/WeatherCard';
import { useAppSelector } from '../hooks';
import { ForecastTable } from '../components/ForecastTable';
import styles from '../styles/forecast-page.module.scss';
export const ForecastPage: React.FC = () => {
	const location = useLocation();
	const todayForecastKey = useAppSelector((state) =>
		Object.keys(state.forecast.forecast).find(
			(key) => key === location.state.date
		)
	) as string;
	const todayForecast = useAppSelector(
		(state) => state.forecast.forecast[todayForecastKey]
	);

	return (
		<>
			<h1 className={styles.title}>{location.state.date} 3 Hours Forecast</h1>
			<WeatherCard weather={location.state} />
			{todayForecast && <ForecastTable todayForecast={todayForecast} />}
		</>
	);
};
