import { Card } from "primereact/card";
import React from "react";
import styled from "styled-components";
import { WeatherDisplay } from "../store";
import { TempDisplay } from "./TempDisplay";
import { Image } from "primereact/image";

type MiniWeatherCardsProps = {
  forecast: WeatherDisplay;
};

const StyledCard = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  background-color: aqua;
  width: 100px;
  height: fit-content;
  color: black;
  border-radius: 10%;
  padding: 12px 0;
`;

const StyledCardDate = styled.span`
  font-size: 18px;
  font-weight: 500;
  margin-bottom: 3px;
  padding-left: 18px;
`;

const StyledTemp = styled(TempDisplay)`
  font-size: 24px;
  font-weight: 600;
  padding-left: 18px;
`;

const StyledWeatherIcon = styled(Image)`
  img {
    width: 50px;
    height: 50px;
  }
`;

export const MiniWeatherCard: React.FC<MiniWeatherCardsProps> = ({
  forecast,
}) => {
  return (
    <StyledCard data-testid="mini-weather-card">
      <StyledCardDate>{forecast.time}</StyledCardDate>
      <StyledWeatherIcon
        src={forecast.icon}
        alt={`${forecast.description} icon`}
      />
      <StyledTemp temp={forecast.temp} />
    </StyledCard>
  );
};
