import React, { HTMLAttributes } from "react";
import {
  CELSIUS_SYMBOL,
  FAHRENHEIT_SYMBOL,
  IMPERIAL_UNIT,
  KELVIN_SYMBOL,
  METRIC_UNIT,
  STANDARD_UNIT,
} from "../common/constants";
import { useAppSelector } from "../hooks";

const unitToSymbol: { [key: string]: string } = {
  [STANDARD_UNIT]: KELVIN_SYMBOL,
  [METRIC_UNIT]: CELSIUS_SYMBOL,
  [IMPERIAL_UNIT]: FAHRENHEIT_SYMBOL,
};

type TempDisplayProps = { temp: number } & Omit<
  HTMLAttributes<HTMLSpanElement>,
  ""
>;
export const TempDisplay: React.FC<TempDisplayProps> = ({
  temp,
  className,
}) => {
  const isLoading = useAppSelector((state) => state.weather.isLoading);
  const unit = useAppSelector((state) => state.weather.units);

  return (
    <span className={className}>
      {isLoading ? "--" : temp}&deg;{unitToSymbol[unit]}
    </span>
  );
};
