import React from "react";
import { useParams } from "react-router-dom";
import { WeatherCard } from "../components/WeatherCard";
import { useAppSelector } from "../hooks";
import { ForecastTable } from "../components/ForecastTable";
import styles from "../styles/forecast-page.module.scss";
import { convertMonthDayToId } from "../utils";
export const ForecastPage: React.FC = () => {
  const { id } = useParams();
  const selectedDayForecast = useAppSelector(
    (state) => state.weather.weather[id],
  );

  return (
    <>
      <h1 className={styles.title}>
        {selectedDayForecast?.date} 3 Hours Forecast
      </h1>
      <WeatherCard weather={isToday ? todayWeather : selectedDayForecast} />
      {selectedDayWeather && (
        <ForecastTable todayForecast={selectedDayWeather} />
      )}
    </>
  );
};
