import styles from "../styles/components/weather-card.module.scss";
import React from "react";
import { useNavigate } from "react-router-dom";
import { TempDisplay } from "./TempDisplay";
import { WeatherDisplay } from "../store";
import { Card } from "primereact/card";
import styled from "styled-components";

type WeatherCardType = {
  id?: string;
  weather: WeatherDisplay;
};

const StyledCard = styled(Card)`
  .p-body-card {
    display: flex;
    flex-direction: column;
    border-radius: 5%;
    min-width: 250px;
    min-height: 220px;
    color: white;
    background-color: blue;
    padding: 18px 18px 18px 0;
    cursor: pointer;
  }
`;

export const WeatherCard: React.FC<WeatherCardType> = ({ weather, id }) => {
  const navigate = useNavigate();
  const redirectToForecast = () =>
    id ? navigate(`/weather-forecast/${id}`) : null;

  return (
    <Card
      className={`${styles.weatherCard} ${!id ? styles.forecastCard : ""}`}
      onClick={redirectToForecast}
    >
      <span className={styles.cardDate}>{weather.date}</span>
      <TempDisplay className={styles.temp} temp={weather.temp} />
      <img src={weather.icon} alt={`${weather.description} icon`} />
      <span className={styles.description}>{weather.description}</span>
    </Card>
  );

  // return (
