import styles from '../../styles/weather-card.module.scss';
import React from 'react';
import { HourlyForecastDisplay, WeatherDisplay } from '../../store/types';
import { useNavigate } from 'react-router-dom';
import { TempDisplay } from '../TempDisplay';

type WeatherCardType = {
	weather: WeatherDisplay | HourlyForecastDisplay;
};

export const WeatherCard: React.FC<WeatherCardType> = ({ weather }) => {
	const navigate = useNavigate();
	const redirectToForecast = () => {
		navigate('/weather-forecast', { state: weather });
	};

	if (!weather) return null;

	return (
		<div className={styles.weatherCard} onClick={redirectToForecast}>
			<span className={styles.cardDate}>{weather.date}</span>
			<TempDisplay className={styles.temp} temp={weather.temp} />
			<img src={weather.icon} alt={`${weather.description} icon`} />
			<span className={styles.description}>{weather.description}</span>
		</div>
	);
};
