import { convertToHoursMinutes, convertToMonthDay } from "./date.util";
import { WeatherResponse } from "../api";
import { ForecastData } from "../api/types";

export const parseWeatherObject = (data: WeatherResponse) => {
  const { description, icon } = data.weather[0];
  const date = convertToMonthDay();
  const id = date.split(" ").join("-").toLowerCase();

  return {
    id,
    date,
    icon: `${process.env.REACT_APP_WEATHER_ICONS_URL}/${icon}@2x.png`,
    description: description.charAt(0).toUpperCase() + description.slice(1), // make the first letter of the description upper case
    temp: Math.round(data.main.temp),
  };
};

export const parseForecastObject = (data: ForecastData) => {
  const { description, icon, main } = data.weather[0];
  const date = convertToMonthDay(data.dt_txt);
  const id = date.split(" ").join("-").toLowerCase();

  return {
    id,
    date,
    time: convertToHoursMinutes(data.dt_txt),
    icon: `${process.env.REACT_APP_WEATHER_ICONS_URL}/${icon}@2x.png`,
    description: description.charAt(0).toUpperCase() + description.slice(1),
    main,
    temp: Math.round(data.main.temp),
  };
};
