import React from 'react';
import styles from '../styles/main-layout.module.scss';
import { MetricSystemSelector } from '../components/MetricSystemSelector';
import { useAppSelector } from '../hooks';

export const MainLayout: React.FC<{ children: React.ReactNode }> = ({
	children,
}) => {
	const isWeatherLoading = useAppSelector(
		(state) => state.weather.isWeatherLoading
	);
	const isForecastLoading = useAppSelector(
		(state) => state.forecast.isForecastLoading
	);

	return (
		<div className={styles.mainContainer}>
			{isWeatherLoading || isForecastLoading ? (
				<div className={styles.loader}>Loading</div>
			) : (
				<>
					<MetricSystemSelector />
					{children}
				</>
			)}
		</div>
	);
};
