import React from "react";
import styles from "../styles/pages/days-weather-page.module.scss";
import { useAppSelector } from "../hooks";
import { WeatherCard } from "../components";
import styled from "styled-components";
import { Skeleton } from "primereact/skeleton";

const StyledWeathersContainer = styled.div`
  display: flex;

  & > div:not(:last-child) {
    margin-right: 5rem;
  }
`;

export const DaysWeatherPage = () => {
  const isLoading = useAppSelector((state) => state.weather.isLoading);
  const daysWeather = useAppSelector((state) => state.weather.weather);
  const weatherTemplate = () =>
    isLoading
      ? Array(5).map(() => <Skeleton width="10rem" height="10rem" />)
      : Object.entries(daysWeather).map(([id, { weather }]) => (
          <WeatherCard key={id} id={id} weather={weather} />
        ));

  return (
    <div className={styles.daysWeather}>
      <h1 className={styles.title}>5 Days Weather</h1>
      <StyledWeathersContainer>{weatherTemplate()}</StyledWeathersContainer>
    </div>
  );
};
