import React, { SyntheticEvent } from "react";
import {
  IMPERIAL_UNIT,
  METRIC_UNIT,
  STANDARD_UNIT,
  WEATHER_METRIC_KEY,
} from "../common/constants";
import { useAppDispatch } from "../hooks";

export const MetricSystemSelector: React.FC = () => {
  const dispatch = useAppDispatch();
  const currentUnit = localStorage.getItem(WEATHER_METRIC_KEY) || STANDARD_UNIT;
  const handleSelectMetric = (event: SyntheticEvent<HTMLSelectElement>) => {
    const target = event.target as HTMLSelectElement;
    dispatch();
    localStorage.setItem(WEATHER_METRIC_KEY, target.value);
  };

  return (
    <select defaultValue={currentUnit} onChange={handleSelectMetric}>
      <option value={STANDARD_UNIT}>Kelvin</option>
      <option value={METRIC_UNIT}>Celsius</option>
      <option value={IMPERIAL_UNIT}>Fahrenheit</option>
    </select>
  );
};
