import React from "react";
import { TempDisplay } from "./TempDisplay";
import { WeatherDisplay } from "../store";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Image } from "primereact/image";

type ForecastTableProps = {
  forecast: WeatherDisplay[];
};

export const ForecastTable: React.FC<ForecastTableProps> = ({ forecast }) => {
  const timeBody = (weather: WeatherDisplay) => weather.time;
  const tempBody = (weather: WeatherDisplay) => (
    <TempDisplay temp={weather.temp} />
  );
  const iconBoyd = (weather: WeatherDisplay) => (
    <Image src={weather.icon} alt={`${weather.main} icon`} />
  );
  const descBody = (weather: WeatherDisplay) => <span>{weather.main}</span>;

  return (
    <DataTable value={forecast} tableStyle={{ minWidth: "60rem" }}>
      <Column field="name" header="Time" body={timeBody}></Column>
      <Column header="Temp" body={tempBody}></Column>
      <Column field="price" header="Icon" body={iconBoyd}></Column>
    </DataTable>
  );
};

