import styles from "../../styles/weather-card.module.scss";
import React from "react";
import { useNavigate } from "react-router-dom";
import { TempDisplay } from "../TempDisplay";
import { WeatherDisplay } from "../../store";
import { useAppSelector } from "../../hooks";

type WeatherCardType = {
  id?: string;
  weather: WeatherDisplay;
};

export const WeatherCard: React.FC<WeatherCardType> = ({ weather, id }) => {
  const navigate = useNavigate();
  const isLoading = useAppSelector((state) => state.weather.isLoading);
  const redirectToForecast = () =>
    id ? navigate(`/weather-forecast/${id}`) : null;

  if (!weather) return null;

  return (
    <div className={styles.weatherCard} onClick={redirectToForecast}>
      {!isLoading && <span className={styles.cardDate}>{weather.date}</span>}
      <TempDisplay
        isLoading={isLoading}
        className={styles.temp}
        temp={weather.temp}
      />
      {!isLoading && (
        <>
          <img src={weather.icon} alt={`${weather.description} icon`} />
          <span className={styles.description}>{weather.description}</span>
        </>
      )}
    </div>
  );
};
